import React, { Component } from 'react';
import { StyleSheet, Text, View, Dimensions, ScrollView, TextInput } from 'react-native';

const {width, height} = Dimensions.get('window');

export default class Login extends Component {
    constructor(props){
        super(props);
        this.state={
            username:'',
            password:''
        };
    }
    handleUsername(val){this.setState({username:val})}
    handlePassword(val){this.setState({password:val})}

    render(){
        return(
        <ScrollView style={styles.container}>


        <View style={{flex:3, alignItems:'center', justifyContent:'center'}}>
          <Text style={styles.title1}>Welcome Back!</Text>
          <TextInput
            onEndEditing={this.handleUsername}
            value={this.state.username}
            placeholder="User name"
            style={styles.input}></TextInput>
          <TextInput
            onEndEditing={this.handlePassword}
            value={this.state.password}
            placeholder="Password"
            style={styles.input}></TextInput>
        </View>

      </ScrollView>
    );

  }

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFDF5',


  },
  input:{
      flex:1,
      borderColor: '#F28482',
      borderRadius:15,
      borderWidth: 5,
      width: 500,

  },
  header:{
    height: height*1/10, 
    flexDirection: 'row', 
    alignItems:'flex-end',
    justifyContent:'flex-end',
    paddingRight: 150,
    start:0,
  },
  title1:{
    flex: 1,
    paddingTop: 100,
    fontSize: 70,
    paddingLeft: 150,
    textAlign: "flex-start",
    color: '#F28482',
    fontFamily:'Avenir-Heavy',
    alignItems: 'flex-start',
    paddingBottom: 25,
  },
  title2:{
    fontSize: 70,
    paddingLeft: 150,
    textAlign: "flex-start",
    fontFamily:'Avenir-Heavy',
    color:' #92C8BB',
    paddingBottom: 25,
  },
  title3:{
    fontSize: 70,
    paddingLeft: 150,
    textAlign: "flex-start",
    fontFamily:'Avenir-Heavy',
    color: '#FFCA74'
  },
  button1:{
    width: 84,
    fontSize:30,
    fontFamily: 'Avenir-Medium',
    fontColor:'#000000',
    padding: 10,


  },
  button2:{
    width: 100,
    fontSize:30,
    fontFamily: 'Avenir-Medium',
    fontColor:'#000000',
