import React, { Component } from 'react';
import { Modal, StyleSheet, Text, View, Dimensions, ScrollView, TextInput, TouchableOpacity, TouchableHighlight} from 'react-native';
import Popup from "reactjs-popup";

import results from './results';

const {width, height} = Dimensions.get('window');

export default class StudyGroup extends Component {
    constructor(props){
        super(props);
        this.state={
            username:'',
            password:'',
            groupName:'',
            groupNumber:0,
            modalVisible: false,

        };
        this.toggleModal = this.toggleModal.bind(this);
    }
    handleUsername(val){this.setState({username:val})}
    handlePassword(val){this.setState({password:val})}
    toggleModal(visible){
        this.setState({modalVisible: visible})
    }



    render(){

        return(
        <ScrollView style={styles.container}>


        <View style={{flex:3, alignItems:'center', justifyContent:'center'}}>
          <View>
            <Text style={styles.title1}>Study Groups</Text>


            <Modal animationType = {"slide"} transparent = {false}
               visible = {modalVisible}
               onRequestClose = {() => { console.log("Modal has been closed.") } }>

               <View style = {styles.modalView}>
                  <Text style = {styles.modalText}>Modal is open!</Text>

                  <TouchableHighlight onPress = {() => {
                     this.toggleModal(false)}}>

                     <Text style = {styles.text}>Close Modal</Text>
                  </TouchableHighlight>
               </View>
            </Modal>

            <TouchableOpacity style={styles.button2} onPress={()=> this.toggleModal(true)}>
                <Text>Create new group</Text>
            </TouchableOpacity>

          </View>
        </View>

      </ScrollView>
    );

  }

}

